const characters = {
    a: "ɐ",b: "q",c: "ɔ",d: "p",e: "ǝ",f: "ɟ",g: "ƃ",h: "ɥ",i: "ᴉ",j: "ɾ",k: "ʞ",l: "l",m: "ɯ",n: "u",o: "o",p: "d",q: "b",r: "ɹ",s: "s",t: "ʇ",u: "n",v: "ʌ",w: "ʍ",x: "x",y: "ʎ",z: "z",
    ///////
    A: "∀",B: "q",C: "Ɔ",D: "p",E: "Ǝ",F: "Ⅎ",G: "פ",H: "H",I: "I",J: "ſ",K: "ʞ",L: "˥",M: "W",N: "N",O: "O",P: "Ԁ",Q: "Q",R: "ɹ",S: "S",T: "┴",U: "∩",V: "Λ",W: "M",X: "X",Y: "⅄",Z: "Z",
}

function flip(options = {}){

        if(!options.word) throw new TypeError('MonMon Error: Missing word to flip');


            let newWord = '';
                for (let i = options.word.length - 1; i  >= 0; i--) {
                    if(options.word[i] === " ") newWord += " "
                    for (const character of Object.keys(characters)){
                        var flipped = characters[character]
                        if(options.word[i] === character) newWord += flipped					
                    }
                }
                return (newWord)
}
	
module.exports = flip;