const { MessageEmbed } = require('discord.js')

class random {
      async joke(message, options = {}){ 
  
        if (!message) throw new Error('MonMon Err: "message" is not defined')
      
        const axios = require('axios')
        const body = await axios.get('https://api.tovade.xyz/v1/fun/joke')
        const res = body.data
        
        const embed = new MessageEmbed()
		setTitle(options.embedTitle || 'New Joke!')
        setColor(options.embedColor || 'RANDOM')
        setDescription(`**${res.question}** \n \n||${res.answer}||`)
        setFooter(options.embedFooter || `Category: ${res.category}`)
      
        await message.channel.send({embeds: [embed]})
      }   
	  
 //
 
      async quote(message, options ={}){
		  
        if (!message) throw new Error('MonMon Err: "message" is not defined')

        const axios = require('axios')
        const body = await axios.get('https://api.tovade.xyz/v1/fun/quote')
        const res = body.data
		
        const embed = new MessageEmbed()
        setTitle(options.embedTitle || 'New Quote')
        setColor(options.embedColor || 'RANDOM')
        setDescription(`${res.content}`)
        setFooter(options.embedFooter || `Author: ${res.author}`)
		
        await message.channel.send({embeds: [embed]})      
      }
      
  }
  
 module.exports = random;
