function randomCase(options = {}){

        let result = '';
        if (!options.string) throw new TypeError('MonMon Error: A string was not specified.');
        if (typeof options.string !== 'string') {
            throw new TypeError('MonMon Error: Provided string is Invalid.');
        }
        for (const i in options.string) {
            const Random = Math.floor(Math.random() * 2);
            result += Random == 1 ? options.string[i].toLowerCase() : options.string[i].toUpperCase();
        }
        return result;

}
	
module.exports = randomCase;