function randomizeArray(options = {}){

        if(!options.array) throw new TypeError('MonMon Error: A array was not specified.');
		if (typeof options.array !== 'object') {
			throw new TypeError('MonMon Error: The provided array is invalid.');
		}


		let result = Math.floor(Math.random() * options.array.length);
		
        return options.array[result]

}
	
module.exports = randomizeArray;