function randomizeNumber(options = {}){

        if(!options.min) throw new TypeError('MonMon Error: Missing minimum number');
        if(!options.max) throw new TypeError('MonMon Error: Missing maximum number');
	    if (typeof options.min !== 'number' && typeof options.max !== 'number') {
			throw new TypeError('MonMon Error: Provided number data is Invalid');
		}

    
		let result = Math.floor(Math.random() * (options.max - options.min + 1) + options.min)
		
        return result

}
	
module.exports = randomizeNumber;