async function sudo(message, args, options = {}) {
	
        if(!message) throw new Error('MonMon Err: "message" is not defined')
        if(!args) throw new TypeError('MonMon Error: "aegs" is not defined');
	    if(typeof args !== 'string') throw new TypeError('MonMon Error: args must be a string');
        if(!options.member) throw new TypeError('MonMon Error: Missing argument member');

        message.channel.createWebhook(options.member.user.username, {
            avatar: options.member.user.displayAvatarURL({ dynamic: true })
        }).then(webhook => {
            webhook.send(args);
            setTimeout(() => {
                webhook.delete();
            }, 3000);
        })
    
}

module.exports = sudo;