const { MessageEmbed } = require('discord.js')
async function playstore(message, args, options = {}) {
    if (!message) throw new Error('MonMon Err: "message" is not defined')
    if (!args) throw new Error('MonMon Err: "args" is not defined')

    let app = args.join(" ")

    const axios = require(`axios`)
    const body = await axios.get(`https://api.popcatdev.repl.co/playstore?q=${app}`)
    const res = body.data
    if(app){
		
    const embed = new MessageEmbed()
    .setTitle(options.embedTitle || 'Play Store!')
    .setDescription(`Giving info for ${app}`)
    .setColor(options.embedColor || `ff0000`)
    .addField('**App:**', `${res.title}`, inline = true)
    .addField('**Company:**', `${res.developer}`, inline = true)
    .addField('**Ratings:**', `\`${res.ratings}\``, inline = true)
    .addField('**Downloads:**', `\`${res.installations}\``, inline = true)
    .addField('**Genre:**', `${res.genre}`, inline = true)
    .addField('**URL:**', `${res.url}`, inline = true)
    
    message.channel.send({embeds: [embed]})
	
    } else return message.reply(`give me app name to search`)
}

module.exports = playstore;
